from decimal import *


def bina(a, i):
    multi = a * 2
    part_en = int(multi)
    part_de = abs(multi) - abs(int(part_en))
    bin_str = ""

    while part_de != 0.00:
        i += 1
        multi = a * 2
        part_en = int(multi)
        part_de = abs(multi) - abs(int(part_en))
        a = part_de

        bin_str += str(part_en)
        if i == 1000:
            break
    return bin_str


def dec_rep(binary_str):
    value, decimal = Decimal("0.5"), Decimal("0.0")
    for char in binary_str:
        if char == "1":
            decimal += value
        value *= Decimal("0.5")
    return decimal


def get_delta(true_value, value):
    return (true_value - dec_rep(value))/true_value


x = Decimal("0.33")
w = bina(x, 0)
print(w)
print(dec_rep(w))
print(f"Decimal: {get_delta(x, w)}")
print(f"Decimal (%): {get_delta(x, w) * 100}%")

z = ""
print(z == w)
